(function() {
    'use strict';
    angular.module('prisonMaster', ['prisonMaster.layout', 'ui.router'])
        .config(["$urlRouterProvider", function($urlRouterProvider) {
            $urlRouterProvider.otherwise('/home'); //eventually this should probably be a 404 page...
        }]);
})();

(function() {
    'use strict';
    angular.module('prisonMaster.layout', ['ui.router'])
        .config(["$stateProvider", function($stateProvider) {
            $stateProvider
                .state('home', {
                    url: '/home',
                    templateUrl: '/static/angular_client/app/layout/home.html'
                });
        }]);
})();

(function() {
    'use strict';
    angular.module('prisonMaster.layout').controller('NavBar', navBar);

    function navBar() {
        var ctrl = this;

    }
})();

(function() {
    'use strict';
    angular.module('prisonMaster.layout')
    .directive('pmNavBar', function () {
        return {
            restrict: 'E',
            templateUrl: '/static/angular_client/app/layout/navbar/navbar.html',
            controller: 'NavBar',
            controllerAs: 'navbar',
        };
    });
})();
